etSystemid(int systemid) {		this.systemid = systemid;	}	@Column(name = "UserId", nullable = false, length = 11, columnDefinition = "INT")	public int getUserid() {		return userid;	}	public void setUserid(int userid) {		this.userid = userid;	}	@Column(name = "MaxLevel", nullable = false, length = 11, columnDefinition = "INT")	public int getMaxlevel() {		return maxlevel;	}	public void setMaxlevel(int maxlevel) {		this.maxlevel = maxlevel;	}	@Column(name = "Description", nullable = false, length = 200, columnDefinition = "VARCHAR")	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	@Column(name = "IsSystemDefine", nullable = false, length = 11, columnDefinition = "INT")	public int getIssystemdefine() {		return issystemdefine;	}	public void setIssystemdefine(int issystemdefine) {		this.issystemdefine = issystemdefine;	}	@Column(name = "Inactive", nullable = false, length = 11, columnDefinition = "INT")	public int getInactive() {		return inactive;	}	public void setInactive(int inactive) {		this.inactive = inactive;	}	@Column(name = "Creator", nullable = false, length = 11, columnDefinition = "INT")	public int getCreator() {		return creator;	}	public void setCreator(int creator) {		this.creator = creator;	}	@Column(name = "CreateTime", nullable = false, length = 19, columnDefinition = "DATETIME")	public Date getCreatetime() {		return createtime;	}	public void setCreatetime(Date createtime) {		this.createtime = createtime;	}	@Column(name = "LastEditor")	public int getLasteditor() {		return lasteditor;	}	public void setLasteditor(int lasteditor) {		this.lasteditor = lasteditor;	}	@Column(name = "LastEditTime", nullable = false, length = 19, columnDefinition = "DATETIME")	public Date getLastedittime() {		return lastedittime;	}	public void setLastedittime(Date lastedittime) {		this.lastedittime = lastedittime;	}	@Column(name = "Version", nullable = false, length = 11, columnDefinition = "INT")	public int getVersion() {		return version;	}	public void setVersion(int version) {		this.version = version;	}	// override toString Method	public String toString() {		StringBuffer sb = new StringBuffer();		sb.append("{");		sb.append("'id':'" + this.getId() + "',");		sb.append("'name':'" + this.getName() + "',");		sb.append("'rootitemid':'" + this.getRootitemid() + "',");		sb.append("'systemid':'" + this.getSystemid() + "',");		sb.append("'userid':'" + this.getUserid() + "',");		sb.append("'maxlevel':'" + this.getMaxlevel() + "',");		sb.append("'description':'" + this.getDescription() + "',");		sb.append("'issystemdefine':'" + this.getIssystemdefine() + "',");		sb.append("'inactive':'" + this.getInactive() + "',");		sb.append("'creator':'" + this.getCreator() + "',");		sb.append("'createtime':'" + this.getCreatetime() + "',");		sb.append("'lasteditor':'" + this.getLasteditor() + "',");		sb.append("'lastedittime':'" + this.getLastedittime() + "',");		sb.append("'version':'" + this.getVersion() + "'");		sb.append("}");		return sb.toString();	}}